using GS.Common.Configuration;  //  

AppSettingsHelper.Initialize("SPC", "ARSTools");    // program.cs  setting    1

  enum    
// SettingKeys   enum  Key   

public enum SettingKeys
{
	None,
	SPSIp,
	SPSPort,
	PostalCenter,
}

SettingKeys.SPSIp.ToAppDataByKey("192.168.254.107");  // set setting
var spsIp = SettingKeys.SPSIp.FromAppDataByKey(string.Empty);  // get setting
//     %AppData%\Gachisoft\ARSToos.settings 
// 192.168.254.107 
//        

var spsPort = SettingKeys.SPSPort.GetInt();
//   app.config         
// 
//    
// 

  enum    
// SettingKeys   enum  Key   

SettingKeys.SPSIp.ToAppData();  // set setting
var spsIp = SettingKeys.None.FromAppData(SettingKeys.None);  // get setting

//     %AppData%\Gachisoft\ARSToos.settings 
// SPSIp
//        

string key  
AppSettingsHelper.ToAppData("PostalCenter", " ");
var value = AppSettingsHelper.FromAppData("PostalCenter", string.Empty);  //    "" 
//     %AppData%\Gachisoft\ARSToos.settings 
