dgvMain.SetDragDropAction((before, after) =>
{
	DataGridViewRow rowToMove = e.Data.GetData(typeof(DataGridViewRow)) as DataGridViewRow;

	dataGridView.Rows.RemoveAt(before);
	dataGridView.Rows.Insert(after, rowToMove);
});

